mapper.select:
select 
from sys_user
where account = #{account} and password=#{password}

select id, account, create_time createTime, password, address, mobile, sex, is_delete isDelete, username FROM sys_user
username like concat('%', #{account}, '%')
mobile like concat('%', #{mobile}, '%')
sex = #{sex}
order by ${order} limit #{pageNo},#{pageSize};
